pub enum Listeners {
    None,
    Registered(u32),
    Pending(Box<[Option<Rc<dyn Listener>>]>),
}

pub enum Attributes {
    Static(&'static [[&'static str; ]]),
    Dynamic {
        keys: &'static [&'static str],
        values: Box<[Option<AttrValue>]>,
    },
    IndexMap(IndexMap<&'static str, AttrValue>),
}
